import React, { useState, useEffect, useLayoutEffect } from 'react';
import { Link } from "react-router-dom";

import './CSS/Navbar.css';

export default function Navbar(){
    const [isNarrowScreen, setIsNarrowScreen] = useState(false);
    
    // Effect Hook to chane type of Navbar, depending on the screen size (screen-width smaller than 575px -> Dropdown-Navbar; else -> Casual Line-Navbar).
    // Important, because screen width can dynamically change, when e.g. rotating the screen. Otherwise Layout might break
    useEffect(() => {
      // set initial value
      const mediaWatcher = window.matchMedia("(max-width: 575px)")
      setIsNarrowScreen(mediaWatcher.matches);
  
      //watch for updates
      function updateIsNarrowScreen(e) {
        setIsNarrowScreen(e.matches);
      }
      mediaWatcher.addEventListener('change', updateIsNarrowScreen)
  
      // clean up after ourselves
      return function cleanup() {
        mediaWatcher.removeEventListener('change', updateIsNarrowScreen)
      }
    });
    
    return(
        <nav className="navbar">
            <HomeBtn/>

            <NavContainer>

            </NavContainer>
        </nav>
    );
}


function NavContainer(params){
    const dropdownBtn = (
        <button className="dropdown-btn-icon">
            <img className="dropdown-btn-icon-img" src="DropdownIcon.png"/>
        </button>
    );
    
    React.Children.map(children, (child, i) => {
        if(params.isDropdown){
            if(child.getType() == "Button") return <DropdownItem title={child.getTitle()} link={child.getLink()}/>;
            else if (child.getType() == "Dropdown" ) return <NestedDropdown title={child.getTitle()}/>;
            else return <DropdownItem title={"   " + child.getTitle()} link={child.getLink()}/>;
        }
        else{
            if(child.getType() == "Button") return <NavbarBtn title={child.getTitle()} link={child.getLink()}/>;
            else if (child.getType() == "Dropdown" ) return <Dropdown title={child.getTitle()}/>;
            else return <DropdownItem title={child.getTitle()} link={child.getLink()}/>;
        }
    })
}


function LineNavbar(){
    const playlistDropdownBtn = <button className="dropdown-btn-text">Playlists</button>;

    return (
        <nav className="navbar">
            <HomeBtn/>

            <div className="navbar-buttons">
                <NavbarBtn link="/" title="Startseite"/>

                <NavbarBtn link="/gemeinde" title="Gemeinde"/>

                <NavbarBtn link="/neuigkeiten" title="Neuigkeiten"/>

                <NavbarBtn link="/predigten" title="Predigten"/>

                <Dropdown title="Playlists" button={playlistDropdownBtn}>
                    <DropdownItem title="Konfliktlösung" link="konfliktloesung"/>
                </Dropdown>

                <NavbarBtn link="/kontakt" title="Kontakt"/>
            </div>
        </nav>
    );
}

function DropdownNavbar(){
    const dropdownBtn = (
        <button className="dropdown-btn-icon">
            <img className="dropdown-btn-icon-img" src="DropdownIcon.png"/>
        </button>
    );

    return(
        <nav className="navbar">
            <HomeBtn/>

            <Dropdown button={dropdownBtn}>
                    <DropdownItem title="Startseite" link="/"/>
                    <DropdownItem title="Gemeinde" link="/gemeinde"/>
                    <DropdownItem title="Neuigkeiten" link="/neuigkeiten"/>
                    <DropdownItem title="Predigten" link="/predigten"/>

                    <NestedDropdown title="Playlist">
                        <DropdownItem title="   Konfliktlösung" link="/konfliktloesung"/>
                    </NestedDropdown>

                    <DropdownItem title="Kontakt" link="/kontakt"/>
            </Dropdown>
        </nav>
    );
}


function NavbarBtn(params){
    return(
        <Link to={params.link} className="navbar-btn-shell">
            <button className="navbar-btn btn-clickable">{params.title}</button>
        </Link>
    );
}

function HomeBtn(){
    return(
        <Link to="/" className="home-btn-shell">
            <button className="home-btn" ><img className="home-btn-image" src="fbg-bremen_icon-white-fg.png" alt="FBG-Bremen Icon"/></button>
        </Link>
    );
}


let closeDropdown;

function Dropdown(params){
    const [isOverButton, setIsOverButton] = useState(false);
    const [isOverList, setIsOverList] = useState(false);
    const [isOpen, setIsOpen] = useState();
    const [animation, setAnimation] = useState(0);
    closeDropdown = () => { setIsOpen(false); setIsOverList(false); setIsOverButton(false); }

    useLayoutEffect(() => {
        if (isOpen && !isOverButton && !isOverList) {
            setIsOpen(false);
        } else if (!isOpen && (isOverButton || isOverList)) {
            setIsOpen(true);
            setAnimation(0);
        }
    }, [isOverButton, isOverList, isOpen]);

    return (
        <div className="dropdown-btn-shell">
            <div className="dropdown-btn" onMouseEnter={() => { setIsOverButton(true); }} onMouseLeave={() => { setIsOverButton(false); } } onClick={() => { setIsOpen(!isOpen); setIsOverButton(!isOpen); setIsOverList(false); }}>
                    {params.button}
            </div>

            {
                isOpen &&
                <div onMouseEnter={() => { setIsOverList(true); }} onMouseLeave={() => { setIsOverList(false); } }>
                    <DropdownMenu animIdx={animation}>
                        {params.children}
                    </DropdownMenu>
                </div>
            }
        </div>
    );
}

function NestedDropdown(params){
    let [isOpen, setIsOpen] = useState();

    return (
        <>
            <div className="dropdown-list-title-panel btn-clickable" onClick={() => { setIsOpen(!isOpen); }}>
                <button className="dropdown-item">{params.title}</button>
                <img className="dropdown-list-title-img" src="ArrowDownIcon.png" alt="Arrow down Icon"/>
            </div>

            {
                isOpen && params.children
            }
        </>
    );
}

function DropdownMenu(params){
    return(
        <div className="dropdown-menu" animation={params.animIdx}>
            {params.children}
        </div>
    );
}
function DropdownItem(params){
    return(
        <Link to={params.link} className="dropdown-item-shell">
            <button className="dropdown-item btn-clickable" onClick={() => { if(closeDropdown != null) {closeDropdown(); console.log("Close"); } }}>{params.title}</button>
        </Link>
    );
}



function ButtonEntity(props){
    this.getType = () => {
        return "Button";
    }

    this.getTitle = () => {
        return props.title;
    }

    this.getLink = () => {
        return props.link;
    }
}
function DropdownEntity(props){
    this.getType = () => {
        return "Dropdown";
    }

    this.getTitle = () => {
        return props.title;
    }
}
function DropdownChild(props){
    this.getType = () => {
        return "Child";
    }

    this.getTitle = () => {
        return props.title
    }

    this.getLink = () => {
        return props.link;
    }
}
