import { useEffect, useState } from "react";

import { Title, SubTitle } from "../Components.js";
import "./CSS/Homepage.css";
import { getProperty, lineToHtml, lineToLink } from "../DataHandler.js";

export default function Homepage(){
    document.title = "FBG Bremen";

    return (
        <>
            <Title title="FBG-Bremen"/>
            <div className="body">
                <div className="pagePreview-panel">
                    <PagePreview title="Gottesdienste"/>
                    <PagePreview title="Neuigkeiten"/>
                    <PagePreview title="Predigten"/>
                    <PagePreview title="Kontakt"/>
                </div>

                <BreakingNews/>
            </div>
        </>
    );
}

function PagePreview(params){
    return(
        <div className="pagePreview">
            <h3>{params.title}</h3>
        </div>
    );
}

function BreakingNews(){
    const [data, setData] = useState();

    useEffect(() => {
        getProperty("neuigkeiten").then(function(content) { setData(content); });
    }, []);

    if(data === undefined) return;

    let content = data.neuigkeiten.map((obj) => {
        return <UpcomingEvent title={obj.title} imgSrc={obj.imgSrc} key={obj.title}>
            <h3>{obj.date}{obj.endDate !== "" && (" - " + obj.endDate)}</h3>
            <p className="eventText">{obj.text.map((line) => {
                if(line.slice(0, 6) === "<link>") return lineToLink(line);
                return lineToHtml(line);
            })}</p>
        </UpcomingEvent>
    })

    return (
        <>
            <SubTitle title="Eilmeldung"/>

            {content}
        </>
    );
}
