import React, { useState, useEffect, useLayoutEffect, useDebugValue, createContext } from 'react';
import { Link } from "react-router-dom";

import './CSS/Navbar.css';


const dropdownButton = {
    dropdownNav: (
        <button className="dropdown-btn-icon">
            <img className="dropdown-btn-icon-img" src="DropdownIcon.png" alt="Dropdown Icon"/>
        </button>
    ),
    lineNav: (
        <button className="dropdown-btn-text">Playlists</button>
    )
}
const dropdownButtonContext = createContext(dropdownButton);


export default function Navbar(){
    let isNarrowScreen = useIsNarrowScreen();

    return(
        <nav className="navbar">
            <dropdownButtonContext.Provider value={isNarrowScreen ? dropdownNav : lineNav}>
                <HomeBtn/>

                <NavContainer isDropdown={isNarrowScreen}>
                    <NavbarItem type="Button" title="Startseite" link="/"/>

                    <NavbarItem type="Button" title="Gemeinde" link="/gemeinde"/>

                    <NavbarItem type="Button" title="Neuigkeiten" link="/neuigkeiten"/>

                    <NavbarItem type="Button" title="Predigten" link="/predigten"/>

                    <NavbarItem type="Dropdown" title="Playlists">
                        <NavbarItem type="Child" title="KonfliktlÃ¶sung" link="konfliktloesung"/>
                    </NavbarItem>

                    <NavbarItem type="Button" title="Kontakt" link="/kontakt"/>
                </NavContainer>
            </dropdownButtonContext.Provider>
        </nav>
    );
}

function useIsNarrowScreen(){
    const [isNarrowScreen, setIsNarrowScreen] = useState(false);
    
    // Effect Hook to chane type of Navbar, depending on the screen size (screen-width smaller than 575px -> Dropdown-Navbar; else -> Casual Line-Navbar).
    // Important, because screen width can dynamically change, when e.g. rotating the screen. Otherwise Layout might break
    useEffect(() => {
      // set initial value
      const mediaWatcher = window.matchMedia("(max-width: 575px)")
      setIsNarrowScreen(mediaWatcher.matches);
  
      //watch for updates
      function updateIsNarrowScreen(e) {
        setIsNarrowScreen(e.matches);
      }
      mediaWatcher.addEventListener('change', updateIsNarrowScreen)
  
      // clean up after ourselves
      return function cleanup() {
        mediaWatcher.removeEventListener('change', updateIsNarrowScreen)
      }
    });

    return isNarrowScreen;
}


function NavContainer(params){
    if(params.isDropdown) {
        return <Dropdown>
            <NavContent> {React.Children.map(params.children, (child, i) => { return child; })} </NavContent>
        </Dropdown>
    }
    else {
        return <div className="navbar-buttons">
            <NavContent> {React.Children.map(params.children, (child, i) => { return child; })} </NavContent>
        </div>
    }
}

function NavContent(params){
    const content = React.Children.toArray(params.children);
    return MapToObj(content, params.isDropdown, params.dropdownBtn);
}

function MapToObj(objs, isDropdown, dropdownBtn){
    return objs.map((obj) => {
        if(obj === undefined || obj.props === undefined) return null;
        
        if(isDropdown){
            if(obj.props.type === "Button") return <DropdownItem title={obj.props.title} link={obj.props.link}  key={obj.props.title}/>;
            else if (obj.props.type === "Dropdown" ) return <NestedDropdown title={obj.props.title}  key={obj.props.title}>{ MapToObj(React.Children.toArray(obj.props.children), isDropdown, dropdownBtn) }</NestedDropdown>;
            else return <DropdownItem title={"   " + obj.props.title} link={obj.props.link}  key={obj.props.title}/>;
        }
        else{
            if(obj.props.type === "Button") return <NavbarBtn title={obj.props.title} link={obj.props.link}  key={obj.props.title}/>;
            else if (obj.props.type === "Dropdown") return <Dropdown title={obj.props.title} button={dropdownBtn} key={obj.props.title}>{ MapToObj(React.Children.toArray(obj.props.children), isDropdown, dropdownBtn) }</Dropdown>;
            else return <DropdownItem title={obj.props.title} link={obj.props.link} key={obj.props.title}/>;
        }
    });
}


function NavbarBtn(params){
    return(
        <Link to={params.link} className="navbar-btn-shell">
            <button className="navbar-btn btn-clickable">{params.title}</button>
        </Link>
    );
}

function HomeBtn(){
    return(
        <Link to="/" className="home-btn-shell">
            <button className="home-btn" ><img className="home-btn-image" src="fbg-bremen_icon-white-fg.png" alt="FBG-Bremen Icon"/></button>
        </Link>
    );
}


let closeDropdown;

function Dropdown(params){
    const [isOverButton, setIsOverButton] = useState(false);
    const [isOverList, setIsOverList] = useState(false);
    const [isOpen, setIsOpen] = useState();
    const [animation, setAnimation] = useState(0);
    closeDropdown = () => { setIsOpen(false); setIsOverList(false); setIsOverButton(false); }

    useLayoutEffect(() => {
        if (isOpen && !isOverButton && !isOverList) {
            setIsOpen(false);
        } else if (!isOpen && (isOverButton || isOverList)) {
            setIsOpen(true);
            setAnimation(0);
        }
    }, [isOverButton, isOverList, isOpen]);

    return (
        <div className="dropdown-btn-shell">
            <div className="dropdown-btn" onMouseEnter={() => { setIsOverButton(true); }} onMouseLeave={() => { setIsOverButton(false); } } onClick={() => { setIsOpen(!isOpen); setIsOverButton(!isOpen); setIsOverList(false); }}>
                    {params.button}
            </div>

            {
                isOpen &&
                <div onMouseEnter={() => { setIsOverList(true); }} onMouseLeave={() => { setIsOverList(false); } }>
                    <DropdownMenu animIdx={animation}>
                        {params.children}
                    </DropdownMenu>
                </div>
            }
        </div>
    );
}

function NestedDropdown(params){
    let [isOpen, setIsOpen] = useState();

    return (
        <>
            <div className="dropdown-list-title-panel btn-clickable" onClick={() => { setIsOpen(!isOpen); }}>
                <button className="dropdown-item">{params.title}</button>
                <img className="dropdown-list-title-img" src="ArrowDownIcon.png" alt="Arrow down Icon"/>
            </div>

            {
                isOpen && params.children
            }
        </>
    );
}

function DropdownMenu(params){
    return(
        <div className="dropdown-menu" animation={params.animIdx}>
            {params.children}
        </div>
    );
}
function DropdownItem(params){
    return(
        <Link to={params.link} className="dropdown-item-shell">
            <button className="dropdown-item btn-clickable" onClick={() => { if(closeDropdown != null) {closeDropdown(); console.log("Close"); } }}>{params.title}</button>
        </Link>
    );
}



function NavbarItem(props){
    this.getType = function () {
        return props.type;
    }

    this.getTitle = function () {
        return props.title;
    }

    this.getLink = function () {
        return props.link;
    }
}
