import { Link } from "react-router-dom";

import { Title } from '../Components';
import { strRemoveUmlauts } from "../DataHandler";

import './CSS/Themen.css'

export default function Predigtreihen(props){
    document.title = "FBG Bremen - Predigtreihen";

    return (
        <>
            <Title title="Predigtreihen"/>

            {props.data !== undefined && <ThemenBlock data={props.data.playlists}/>}
        </>
    );
}

function ThemenBlock(props){
    return props.data.map((playlist) => { return <ThemaButton theme={playlist.theme} key={playlist.theme}/> });
}

function ThemaButton(props){
    return(
        <Link to={'/' + strRemoveUmlauts(props.theme.toLowerCase())}>
            <button>
                
            </button>
        </Link>
    );
}
