import { Title, SubTitle } from "../Components.js";
import './CSS/Neuigkeiten.css';

export default function Neuigkeiten(){
    return (
        <>
            <Title title="Neuigkeiten"/>
            
            <UpcomingEvent title="Kinderfreizeit" imgSrc="News Content/GrosseHoehe.jpg">
                <p className="eventText">
                    Die Kinderfreizeit findet jedes Jahr während der Sommerferien statt.
                    In diesem Jahr ist sie vom [Insert Date] bis zum [Insert Date].<br/>
                    Dabei hausieren wir in der Jugendherberge "Große Höhe" <a href="http://www.schullandheim-grosse-hoehe.de/" target="_blank">(weitere Informationen gibt es hier)</a>,
                    wo wir gemeinsam in Gottes Wort forschen, beten, singen, basteln und ganz viele Spiele spielen werden.
                    Eingeladen sind alle Kinder im Alter von 6 bis einschließlich 11 Jahren. Der Kosten für die Unterkunft betragen [Insert Price]€.
                </p>
            </UpcomingEvent>
        </>
    );
}

function UpcomingEvent(params){
    return(
        <div>
            <SubTitle title={params.title}/>
            <div className="eventPanel">
                {params.imgSrc != null && <img className="eventImage" src={params.imgSrc}/>}
                {params.imgSrc == null && <div className="eventImage"/>}
                {params.children}
            </div>
        </div>
    );
}
