import { Title, SubTitle } from "../Components.js";
import { LoadJSON } from "../DataHandler.js";

import './CSS/Gottesdienste.css';

export default function Gottesdienste(props){
    let allLanguagesTxt = LoadJSON("Data/gottesdienstTxt.json");
    console.log(allLanguagesTxt);
    let isGerman = props.lan === "ger";

    let contentTxt = allLanguagesTxt.german;
    if(!isGerman) contentTxt = allLanguagesTxt.russian;

    document.title = "FBG Bremen - Gottesdienste";

    return (
        <>
            <Title title="Gottesdienste"/>
            
            <Zeitplan/>

            <SubTitle title="Anfahrt"/>
            <Anfahrt/>
        </>
    );
}

function Zeitplan(props){
    return(
        <div className="zeitplan-shell">
            <table className="zeitplan">
                <tbody>
                    <tr>
                        <td>Mittwoch</td>
                        <td>18 Uhr</td>
                        <td>Bibelstunde</td>
                    </tr>
                    { props.isGerman &&
                    <tr>
                        <td>Donnerstag</td>
                        <td>19:30 Uhr</td>
                        <td>Jugendstunde</td>
                    </tr> }
                    <tr>
                        <td>Freitag</td>
                        <td>18 Uhr</td>
                        <td>Gebetsstunde</td>
                    </tr>
                    <tr>
                        <td>Sonntag</td>
                        <td>10 Uhr</td>
                        <td>Gottesdienst</td>
                    </tr>
                    <tr>
                        <td></td>
                        <td>17 Uhr</td>
                        <td>Gottesdienst</td>
                    </tr>
                </tbody>
            </table>

            <p>
                [Insert Gottesdienst Text here]
            </p>
        </div>
    );
}


function Anfahrt(){
    return(
        <div className="anfahrtPanel">
            <Map/>
            <div className="addressPanel">
                <h3 className="addressParagraph">Adresse</h3>
                <p className="addressParagraph">Uphuser Heerstraße, 102</p>
                <p className="addressParagraph">28832 Achim</p>
            </div>
        </div>
    );
}

function Map(){
    return(
        <iframe className="map" src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2399.286946718227!2d8.953596251892163!3d53.03318007981602!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x47b0df4066c90c79%3A0x6825acbfc5524e8d!2sUphuser%20Heerstra%C3%9Fe%20102%2C%2028832%20Achim!5e0!3m2!1sde!2sde!4v1654288654442!5m2!1sde!2sde" allowFullScreen={true} loading="lazy" referrerPolicy="no-referrer-when-downgrade" title="MapsAnfahrt"></iframe>
    );
}
