import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import './CSS/Main.css'

import { getJSON } from './DataHandler';

import Homepage from './Pages/Homepage';
import AboutUs from './Pages/AboutUs';
import Glaubensbekenntnis from './Pages/Glaubensbekenntnis';
import Neuigkeiten from './Pages/Neuigkeiten';
import Predigten from './Pages/Predigten';
import Predigtreihe from './Pages/Predigtreihe';
import Themen from './Pages/Themen';
import Kontakt from './Pages/Kontakt';
import Impressum from './Pages/Impressum';

export default function Body(){
  let data = LoadJSON();

  return (
    <div className="content">
      <Routes>
        <Route exact path='/' element={<Homepage/>}></Route>
        <Route exact path='/ueber-uns' element={<AboutUs/>}></Route>
        <Route exact path='/glaubensbekenntnis' element={<Glaubensbekenntnis/>}></Route>
        <Route exact path='/neuigkeiten' element={<Neuigkeiten data={data}/>}></Route>
        <Route exact path='/aktuelle-predigten' element={<Predigten/>}></Route>
        <Route exact path='/predigtreihen' element={<Themen data={data}/>}></Route>
        <Route exact path='/kontakt' element={<Kontakt/>}></Route>
        <Route exact path='/impressum' element={<Impressum/>}></Route>
        {data !== undefined && data.playlists.map((playlist) => { return <Route exact path={'/' + playlist.theme.toLowerCase().replace(/\u00e4/g, "ae").replace(/\u00f6/g, "oe").replace(/\u00fc/g, "ue").replace(/\u00df/g, "ss")} element={<Predigtreihe playlist={playlist} key={playlist.theme}/>}/> })}
      </Routes>
    </div>
  );
}

function LoadJSON(){
  const [data, setData] = useState();

  useEffect(() => {
      getJSON().then(function(content) { setData(content); });
  }, []);

  return data;
}
