import { Title, SubTitle } from "../Components.js";
import './CSS/Neuigkeiten.css';

import data from '../Data/news.json';

export default function Neuigkeiten(){
    document.title = "FBG Bremen - Neuigkeiten";

    let content = data.news.map((obj) => {
        return <UpcomingEvent title={obj.title} imgSrc={obj.imgSrc} key={obj.title}>
            <p className="eventText">{obj.text.map((line) => {
                if(line.slice(0, 6) === "<link>"){

                }
                if(!line.includes("\n")) return line;
                return lineToHtml(line);
            })}</p>
        </UpcomingEvent>
    })

    return (
        <>
            <Title title="Neuigkeiten"/>

            { content }

            <UpcomingEvent title="Kinderfreizeit" imgSrc="News Content/GrosseHoehe.jpg">
                <p className="eventText">
                    Die Kinderfreizeit wird jedes Jahr im Sommer abgehalten.
                    In diesem Jahr soll sie vom [Insert Date] bis zum [Insert Date] stattfinden.<br/>
                    Dabei hausieren wir in der Jugendherberge "Große Höhe" <a href="http://www.schullandheim-grosse-hoehe.de/" target="_blank" rel="noreferrer">(Hier geht es zur Website der Herberge)</a>,
                    wo wir gemeinsam in Gottes Wort forschen, beten, singen, basteln und ganz viele Spiele spielen werden.<br/>
                    Eingeladen sind alle Kinder im Alter von 6 bis einschließlich 11 Jahren. Die Kosten für die Unterkunft betragen [Insert Price]€.
                </p>
            </UpcomingEvent>
            
            <UpcomingEvent title="Teeniefreizeit" imgSrc="News Content/GrosseHoehe.jpg">
                <p className="eventText">
                    Die Teeniefreizeit wird jedes Jahr im Sommer abgehalten.
                    In diesem Jahr soll sie vom [Insert Date] bis zum [Insert Date] stattfinden.<br/>
                    Dabei hausieren wir in der Jugendherberge Große Höhe <a href="http://www.schullandheim-grosse-hoehe.de/" target="_blank" rel="noreferrer">(Hier geht es zur Website der Herberge)</a>,
                    wo wir gemeinsam in Gottes Wort forschen, beten, singen, basteln und ganz viele Spiele spielen werden.<br/>
                    Eingeladen sind alle Kinder im Alter von 12 bis einschließlich 15 Jahren. Die Kosten für die Unterkunft betragen [Insert Price]€.
                </p>
            </UpcomingEvent>
            
            <UpcomingEvent title="Jugendfreizeit" imgSrc="News Content/Markushof.jfif">
                <p className="eventText">
                    Die Jugendfreizeit wird jedes Jahr im Sommer abgehalten.
                    In diesem Jahr soll sie vom 16.07.2022 bis zum 23.07.2022 stattfinden.<br/>
                    Dabei besuchen wir den Markushof <a href="http://www.markushof.com/" target="_blank" rel="noreferrer">(Hier geht es zur Website der Herberge)</a>,
                    wo wir gemeinsam in Gottes Wort forschen, beten, singen und ganz viele Spiele spielen werden.<br/>
                    Eingeladen sind alle Jugendliche ab dem Alter von 16 Jahren. Die Kosten für die Unterkunft betragen 283,50€.
                </p>
            </UpcomingEvent>
        </>
    );
}

function lineToHtml(line){
    let content = [];
    let chars = [...line];
    
    for(let i = 0; i < chars.length; i++){
        if(chars[i] === '\n') {
            content.push(<br/>);
            content.oush("");
        }
        else content[content.length - 1] += chars[i];
    }

    return content;
}

function UpcomingEvent(params){
    return(
        <div className="eventPanel">
            <SubTitle title={params.title}/>
            <div className="eventContent">
                {params.imgSrc != null && <img className="eventImage" src={params.imgSrc} alt={params.ingSrc}/>}
                {params.imgSrc == null && <div className="eventImage"/>}
                {params.children}
            </div>
        </div>
    );
}
