import { Title, Video } from '../Components';
import './CSS/Konfliktloesung.css'

export default function Konfliktloesung(){
    document.title = "FBG Bremen - Konfliktlösung";

    return (
        <>
            <Title title="Biblische Konfliktlösung"/>
            <Description>
                <p className="descriptionText">Diese Reihe ist eine Kurzfassung des Gesamten Seminars "Biblische Konfliktlösung" gehalten von Mike Leister. Das Gesamte Seminar können sie sich auf der Seite der Bibelgemeinde NordRhön anhören:</p>
                <a className="descriptionText" href = "https://www.bibelgemeinde.de/downloads/biblische-konfliktl%C3%B6sung/">Zum Seminar der Bibelgemeinde</a>
            </Description>
            <LatestVideos/>
        </>
    );
}

function Description(params){
    return(
        <div className="descriptionArea">
            {params.children}
        </div>
    );
}

function LatestVideos(){
    return (
        <div className="videosPanel">
            <VideoEntry url="Afa8JEXEbnU" title="Einführung" subtitle="1. Teil: Biblische Konfliktlösung"/>
            <VideoEntry url="AfndU9ztbN4" title="Gib Gott die Ehre" subtitle="2. Teil: Biblische Konfliktlösung"/>
            <VideoEntry url="FgqP9SjM7I8" title="Greif zum Balken in deinem Auge" subtitle="3. Teil: Biblische Konfliktlösung"/>
            <VideoEntry url="6-8pZuSze3I" title="Gebrauche Sanftmut zur Zurechtweisung A" subtitle="4. Teil: Biblische Konfliktlösung"/>
            <VideoEntry url="ZQob38KTdL8" title="Gebrauche Sanftmut zur Zurechtweisung B" subtitle="4. Teil: Biblische Konfliktlösung"/>
            <VideoEntry url="Qn48qraaDrs" title="Geh hin und versöhne dich" subtitle={"5. Teil: Biblische Konfliktlösung"}/>
        </div>
    );
}

function VideoEntry(params){
    return(
        <div className="videoEntry">
            <div className="videoContainer">
                <Video url={params.url}/>
            </div>
            <div className="videoTextPanel">
                <h2 className="videoTitle">{params.title}</h2>
                <h3 className="videoSubtitle">{params.subtitle}</h3>
            </div>
        </div>
    );
}
