import { useState } from "react";
import emailjs from "emailjs-com";

import { Title } from "../Components.js";
import { LoadJSON } from "../DataHandler.js";

export default function Kontakt(){
    document.title = "FBG Bremen - Kontakt";

    let contactData = LoadJSON("Data/kontaktdaten.json");
    if(contactData === undefined) return;

    return (
        <div id="kontakt">
            <Title title="Kontakt"/>
            
            <Form/>
        </div>
    );
}

var data = {
    addressee: "unbestimmt",
    name: "",
    email: "",
    subject: "",
    message: ""
}
var setData = null;

function Form(){
    [data, setData] = useState({
        addressee: "unbestimmt",
        email: "",
        message: ""
    })

    return(
        <form className="form" onSubmit={HandleSubmit}>
            <Addressee/>

            <Sender/>

            <Subject/>

            <Message/>
            
            <input type="submit" className="submit" value="Nachricht senden"/>
        </form>
    );
}

function Addressee(){
    return(
        <>
            <label htmlFor="addressat-dropdown">Addressat:</label>

            <select id="addressat-dropdown" className="addressee" name="addressee" value={data.addressee} onChange={HandleInputChange}>
                <option value="unbestimmt">Unbestimmt</option>
                <option value="vorstand">Vorstand</option>
                <option value="aelteste">Ã„lteste</option>
                <option value="technik">Technik</option>
            </select>
        </>
    );
}

function Name(){
    return(
        <>
            <label htmlFor="name-inp">E-Mail:</label>

            <input type="text" id="name-inp" className="name" placeholder="Name..." name="email" value={data.name} onChange={HandleInputChange}></input>
        </>
    );
}

function Sender(){
    return(
        <>
            <label htmlFor="sender-inp">E-Mail:</label>

            <input type="email" id="sender-inp" className="sender" required placeholder="IhreEmail@adresse.de" name="email" value={data.email} onChange={HandleInputChange}></input>
        </>
    );
}

function Subject(){
    return(
        <>
            <label htmlFor="subject-inp">Betreff:</label>

            <input type="text" id="subject-inp" className="subject" placeholder="Thema..." name="subject" value={data.subject} onChange={HandleInputChange}></input>
        </>
    )
}

function Message(){
    return(
        <>
            <label htmlFor="message-inp">Nachricht:</label>

            <input type="text" id="message-inp" className="message" required placeholder="Lieber..." name="message" value={data.message} onChange={HandleInputChange}></input>
        </>
    )
}



function HandleInputChange(event){
    const target = event.target;
    const value = target.value;
    const name = target.name;

    setData({
        ...data,
        [name]: value
    });
}


function HandleSubmit(event){
    event.preventDefault();
    console.log(data);
}


function SendEmail(e) {
    e.preventDefault();    //This is important, i'm not sure why, but the email won't send without it

    emailjs.sendForm('service_ax7mn5q', 'YOUR_TEMPLATE_ID', e.target, 'YOUR_USER_ID')
      .then((result) => {
          window.location.reload()  //This is if you still want the page to reload (since e.preventDefault() cancelled that behavior) 
      }, (error) => {
          console.log(error.text);
      });
  }
