import React,  { useState, useEffect } from 'react';
import { Title, Video } from '../Components';
import './CSS/Predigten.css'

export default function Predigten(){
    return (
        <>
            <Title title="Predigten"/>
            <div class="body">
                <LatestVideos/>
                <YoutubeChannel/>
            </div>
        </>
    );
}

function LatestVideos(){
    const [videos, setVideos] = useState([]);
    var channelId = "UChUaD69cuuTJSUuu_fHJgFQ";

    const baseUrl = 'https://api.rss2json.com/v1/api.json?rss_url=https%3A%2F%2Fwww.youtube.com%2Ffeeds%2Fvideos.xml%3Fchannel_id%3D';

    useEffect(() => {
        (async () => {
            if (channelId) {
                try {
                    const data = await fetch(`${baseUrl}${channelId}`).then(response => response.json());
                    setVideos(data.items);
                } catch (error) {
                    console.log(error);
                }
            }
        })();
    }, [channelId]);


    return (
        <div className="videosPanel_predigt">
            {videos.slice(0, 3).map(video => {
                return(
                    <VideoEntry url={video.link} title={video.title} subtitle={video.description}/>
                );
            })}
        </div>
    );
}

function VideoEntry(params){
    return(
        <div className="videoEntry_predigt">
            <div className="videoContainer_predigt">
                <Video url={params.url}/>
            </div>
            <h3 className="videoTitle_predigt">{params.title}</h3>
        </div>
    );
}


function YoutubeChannel(){
    return(
        <div className="youtubeChannelPanel">
            <a href="https://www.youtube.com/channel/UChUaD69cuuTJSUuu_fHJgFQ">Hier geht es zum YouTube Kanal der Gemeinde, wo sie viele weitere Predigten finden k√∂nnen!</a>
        </div>
    );
}
