import { useEffect, useState } from "react";

import { Title, Video } from '../Components';
import './CSS/Playlist.css'
import { strArrayToText } from '../DataHandler';

export default function Playlist(props){
    document.title = "FBG Bremen - " + props.theme;

    let playlist = props.data.playlists[0];
    props.data.playlists.map((p) => {
        if(p.theme == props.theme) playlist = p;
    })

    return (
        <>
            <Title title={playlist.theme}/>
            <div className="descriptionArea">
                <p className="descriptionText">{ strArrayToText(playlist.description) }</p>
            </div>
            <LatestVideos playlist={playlist}/>
        </>
    );
}

function LatestVideos(props){
    return (
        <div className="videosPanel">
            {props.playlist.entries.map((entry) => {
                return <VideoEntry url={entry.url} title={entry.title} subtitle={entry.subtitle}/>
            })}
        </div>
    );
}

function VideoEntry(props){
    return(
        <div className="videoEntry">
            <div className="videoContainer">
                <Video url={props.url}/>
            </div>
            <div className="videoTextPanel">
                <h2 className="videoTitle">{props.title}</h2>
                {props.subtitle != null && <h3 className="videoSubtitle">{props.subtitle}</h3>}
            </div>
        </div>
    );
}
