import React from 'react';
import './CSS/Main.css'

export function Title(params){
    return(
        <div className="title">
            <h1>{params.title}</h1>
        </div>
    );
}

export function SubTitle(params){
    return(
        <div className="subtitle">
            <h3>{params.title}</h3>
        </div>
    );
}


/*export function YoutubeLink(){
    return(
        <div class="mt-5 pt-4">
            <h4>Hier gibt es noch viele weitere Predigten:</h4>
            <a href="https://www.youtube.com/channel/UChUaD69cuuTJSUuu_fHJgFQ">
                <div class="d-flex flex-column text-center border border-dark mt-5">
                    <div class="pb-2 pt-5">Hier geht's zum YouTube Channel</div>
                    <div class="pb-5 pt-2">Der FBG-Bremen</div>
                </div>
            </a>
        </div>
    );
}*/

export function Video (props){
    return (
        <div className="videoFrame">
            <param name="movie" value={URLtoEmbed(props.url)}></param>
            <embed src={URLtoEmbed(props.url)} type="application/x-shockwave-flash"></embed>
        </div>

        //<iframe className="videoFrame" src={URLtoEmbed(props.url)} title={props.url} allowFullScreen={true}/>
    );
}

function URLtoEmbed(url){
    var startIndex = url.search("v=");
    if(startIndex === -1) return "https://www.youtube.com/embed/" + url;

    return "https://www.youtube.com/embed/" + url.substring(startIndex + 2);
}
