import { useEffect, useState } from "react";

import { Title, Video } from '../Components';
import './CSS/Playlist.css'
import { getProperty, strArrayToText } from '../DataHandler';

export default function Playlist(props){
    document.title = "FBG Bremen - " + props.theme;

    const [data, setData] = useState();

    useEffect(() => {
        getProperty("playlists").then(function(content) { setData(content); });
    }, []);

    if(data === undefined) return;

    let playlist = data.playlists[0];
    data.playlists.map((p) => {
        if(p.theme == props.theme) playlist = p;
    })

    return (
        <>
            <Title title={playlist.theme}/>
            <Description>
                <p className="descriptionText">{ strArrayToText(playlist.description) }</p>
            </Description>
            <LatestVideos/>
        </>
    );
}

function Description(params){
    return(
        <div className="descriptionArea">
            {params.children}
        </div>
    );
}

function LatestVideos(){
    return (
        <div className="videosPanel">
        </div>
    );
}

function VideoEntry(params){
    return(
        <div className="videoEntry">
            <div className="videoContainer">
                <Video url={params.url}/>
            </div>
            <div className="videoTextPanel">
                <h2 className="videoTitle">{params.title}</h2>
                <h3 className="videoSubtitle">{params.subtitle}</h3>
            </div>
        </div>
    );
}
