import { Link } from "react-router-dom";

import { Title, SubTitle, Video } from "../Components.js";
import { LoadJSON, useGetLatestVideos } from "../DataHandler.js";

export default function Homepage(){
    document.title = "FBG Bremen";

    const data = LoadJSON("Data/homepage.json");
    if(data === undefined) return;

    return (
        <div id="homepage">
            <Title title="FBG-Bremen"/>

            <div className="content-field">
                <TopVerse text={data.top_verse.text} passage={data.top_verse.passage}/>

                <SplitLine count={2}/>

                <FuenfSolas/>

                <SplitLine/>

                <GottesdienstePreview/>

                <SplitLine/>

                <PredigtenPreview/>

                <SplitLine/>
            </div>
        </div>
    );
}

function SplitLine(props){
    let count = 1
    if(props.count !== undefined) count = props.count;

    let lines = [];
    for (let i = 0; i < count; i++){
        lines.push(<div className="split-line line" key={i}/>);
    }

    return (
        <div className="split-lines">{lines}</div>
    );
}

function TopVerse(props){
    return (
        <div className="top_verse">
            <p className="text">{props.text}</p>
            <p className="passage">{props.passage}</p>
        </div>
    )
}


function GottesdienstePreview(){
    let gottesdienste = LoadJSON("Data/gottesdienstTxt.json");
    if(gottesdienste === undefined) return;

    let zeitplan = gottesdienste.german.terminTabelle;

    return (
        <div className="flex-column">
            <SubTitle title="Unsere Gottesdienste"/>
            <div className="gottesdienste-preview">
                <div className="zeitplan homepage">
                    <table>
                        <tbody>
                            {zeitplan.map((termin) => {
                                return(
                                    <tr key={termin.tag + termin.uhrzeit}>
                                        <td>{termin.tag}</td>
                                        <td>{termin.uhrzeit}</td>
                                        <td>{termin.typ}</td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
                </div>
                <Link to="/gottesdienste" className="btn-link">
                    <button>
                        <p>Zur Wegbeschreibung</p>
                    </button>
                </Link>
            </div>
            <p className="gottesdienst-invitation">Kommen sie uns gerne mal besuchen!</p>
        </div>
    );
}

function FuenfSolas(){
    return (
        <div className="solas-panel">
            <Sola title="Gottes Wort" sola="Sola Scriptura" icon={process.env.PUBLIC_URL + "/Icons/Scriptura-icon.svg"}/>
            <Sola title="durch Jesus Chrisus" sola="Solus Christus" icon=""/>
            <Sola title="durch Glauben" sola="Sola Fide" icon=""/>
            <Sola title="aus Gnade" sola="Sola Gratia" icon=""/>
            <Sola title="zu Gottes Ehre" sola="Soli Deo Gloria" icon=""/>
        </div>
    );
}

function Sola(props){
    return (
        <div className="sola">
            <img className="icon" src={props.icon} alt={props.sola + " - Icon"}/>

            <div className="topic">
                <p className="title">{props.title}</p>
                <p className="subtitle">{props.sola}</p>
            </div>
        </div>
    );
}


function PredigtenPreview(){
    let video = useGetLatestVideos()[0];
    if (video === undefined) return;

    return (
        <div className="predigten-preview">
            <SubTitle title="Lehre"/>
            <div className="flex-row">
                <div className="video">
                    <Video url={video.link}/>
                    <p>{video.title}</p>
                </div>

                <Link to="/aktuelle-predigten" className="btn-link">
                    <button>
                        <p>Weitere Predigten</p>
                    </button>
                </Link>
            </div>
        </div>
    );
}
