import { Link } from "react-router-dom";

import './CSS/Navbar.css';

export default function Navbar(){
    return (
        <nav className="navbar">
            <NavbarBtn link="/" title="Homepage"/>

            <NavbarBtn link="/gemeinde" title="Gemeinde"/>

            <NavbarBtn link="" title="Neuigkeiten"/>

            <NavbarBtn link="" title="Predigten"/>

            <Dropdown title="Playlists">
                <DropdownMenu>
                    <DropdownItem link="/" title="KonfliktlÃ¶sung"/>
                </DropdownMenu>
            </Dropdown>

            <NavbarBtn link="" title="Kontakt"/>
        </nav>
    );
}

function NavbarBtn(params){
    return(
        <Link to={params.link} className="navbar-btn-shell">
            <button className="navbar-btn">{params.title}</button>
        </Link>
    );
}

function Dropdown(params){
    /*const [open, setOpen] = useState(false);

    function useDropdownClose(ref) {
      useEffect(() => {
        function handleClickOutside(event) {
          if (ref.current && !ref.current.contains(event.target)) {
              setOpen(false);
          }
        }
        // Bind the event listener
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
          // Unbind the event listener on clean up
          document.removeEventListener("mousedown", handleClickOutside);
        };
      }, [ref]);
    }

    const wrapperRef = useRef(null);
    useDropdownClose(wrapperRef);

    return(
        <div className="navbar-btn-shell" ref={wrapperRef}>
            <button className="navbar-btn" onClick={() => setOpen(!open)}>{params.title}</button>

            {open && params.children}
        </div>
    );*/
}

function DropdownMenu(params){
    return(
        <div className="dropdown-menu">
            {params.children}
        </div>
    );
}
function DropdownItem(params){
    return(
        <Link to={params.link} className="dropdown-item-shell">
            <button className="dropdown-item">{params.title}</button>
        </Link>
    );
}
