import { Title } from "../Components.js";
import { LoadJSON } from "../DataHandler.js";
import "./CSS/Kontakt.css";

export default function Kontakt(){
    document.title = "FBG Bremen - Kontakt";

    let contactData = LoadJSON("Data/kontaktdaten.json");
    if(contactData === undefined) return;

    return (
        <>
            <Title title="Kontakt"/>
            <p>E-Mail: info@fbg-bremen.de</p>
            <p>Bei persönlichen Fragen dürfen sie auch gerne eine der folgenden Personen kontaktieren:</p>
            {
                contactData.kontakte.map((kontakt) => {return <Kontaktperson name={kontakt.name} imgSrc={kontakt.imgSrc} email={kontakt.email} key={kontakt.name}/>})
            }
        </>
    );
}

function Kontaktperson(props){
    let imgSrc = props.imgSrc;
    if(imgSrc == null || imgSrc === undefined || imgSrc === "") imgSrc = "Images/Kontaktpersonen/user.png";
    console.log(imgSrc);

    return (
        <div>
            <h3>{props.name}</h3>
            <img src={imgSrc} alt={props.name + "_personImage"}/>
            <p>{props.email}</p>
        </div>
    );
}
