import { Title, SubTitle } from "../Components.js";
import './CSS/Neuigkeiten.css';

import data from '../Data/news.json';

export default function Neuigkeiten(){
    document.title = "FBG Bremen - Neuigkeiten";

    let content = data.news.map((obj) => {
        return <UpcomingEvent title={obj.title} imgSrc={obj.imgSrc} key={obj.title}>
            <p className="eventText">{obj.text.map((line) => {
                if(line.slice(0, 6) === "<link>") return lineToLink(line);
                return lineToHtml(line);
            })}</p>
        </UpcomingEvent>
    })

    return (
        <>
            <Title title="Neuigkeiten"/>

            { content }
        </>
    );
}

function lineToHtml(line){
    let content = [""];
    let chars = [...line];
    
    for(let i = 0; i < chars.length; i++){
        if(chars[i] === '\n') {
            content.push(<br key={i}/>);
            content.push("");
        }
        else content[content.length - 1] += chars[i];
    }

    return content;
}
function lineToLink(line){
    line = line.slice(6);
    let [url, text] = line.split("_text_:");
    return <a href={url} target="_blank" rel="noreferrer">{text}</a>
}

function UpcomingEvent(params){
    return(
        <div className="eventPanel">
            <SubTitle title={params.title}/>
            <div className="eventContent">
                {params.imgSrc != null && <img className="eventImage" src={params.imgSrc} alt={params.ingSrc}/>}
                {params.imgSrc == null && <div className="eventImage"/>}
                {params.children}
            </div>
        </div>
    );
}
