import React, { useState, useEffect, useRef, useLayoutEffect } from 'react';
import { Link } from "react-router-dom";

import './CSS/Navbar.css';

export default function Navbar(){
    return (
        <nav className="navbar">
            <NavbarBtn link="/" title="Homepage"/>

            <NavbarBtn link="/gemeinde" title="Gemeinde"/>

            <NavbarBtn link="" title="Neuigkeiten"/>

            <NavbarBtn link="" title="Predigten"/>

            <Dropdown title="Playlists" entries={[{link: "konfliktloesung", title: "Konfliktlösung"}, {link: "/", title: "Joo"}]}/>

            <NavbarBtn link="" title="Kontakt"/>
        </nav>
    );
}

function NavbarBtn(params){
    return(
        <Link to={params.link} className="navbar-btn-shell">
            <button className="navbar-btn">{params.title}</button>
        </Link>
    );
}

function Dropdown(params){
    let [isOverButton, setIsOverButton] = useState(false);
    let [isOverList, setIsOverList] = useState(false);
    let [isOpen, setIsOpen] = useState();
    let [isTouchInput, setIsTouchInput] = useState();
    let [hasClicked, setHasClicked] = useState();
    let [animation, setAnimation] = useState(0);
    const dropdownMenu = useRef();

    useLayoutEffect(() => {
        if (isOpen && !isOverButton && !isOverList && !isTouchInput) {
            setIsOpen(false);
            setAnimation(1);
        } else if (!isOpen && (isOverButton || isOverList) && !isTouchInput) {
            setIsOpen(true);
            setAnimation(2);
        }
    }, [isOverButton, isOverList]);

    useEffect(() => {
        setIsTouchInput(false);
        setHasClicked(false);
    }, [hasClicked]);

    return (
        <div className="dropdown-btn-shell">
            <div className="dropdown-btn" onTouchStart={() => { setIsTouchInput(true); }} onMouseEnter={() => { setIsOverButton(true); }}
                onMouseLeave={() => { setIsOverButton(false); }} onKeyDown={() => { setIsOpen(!isOpen); }} onClick={() => {if(isTouchInput) setIsOpen(!isOpen);}}>
                <button className="dropdown-btn-text">{params.title}</button>
            </div>

            {
                (isOpen || (!isOpen && dropdownMenu.current != null && dropdownMenu.current.style.opacity > 0)) &&
                <div onMouseEnter={() => { setIsOverList(true); }} onMouseLeave={() => { setIsOverList(false); }}>
                    <DropdownMenu animIdx={animation} reference={dropdownMenu}>
                        {params.entries.map((params) => { return <DropdownItem onSelect={() => { setIsOpen(false); }} link={params.link} title={params.title} key={params.title}/>;})}
                    </DropdownMenu>
                </div>
            }
        </div>
    );
}

function DropdownMenu(params){
    return(
        <div className="dropdown-menu" animation={params.animIdx} ref={params.reference}>
            {params.children}
        </div>
    );
}
function DropdownItem(params){
    return(
        <Link to={params.link} className="dropdown-item-shell">
            <button className="dropdown-item">{params.title}</button>
        </Link>
    );
}
